    $(document).on('keyup', '#game-search', function() {
        var query = $(this).val().toLowerCase();
        if (query) {
            $(".casino-game-item").map(function() {
                if ($('img.lazy', this).attr('alt').toLowerCase().includes(query)) {
                    this.style.display = "block";
                } else {
                    this.style.display = "none";
                }
            })
        } else {
            $(".casino-game-item").css('display', 'block');
        }
    });
