    $('.sport-history').click(function() {
        $.ajax({
            url: "<?= route('frontend.sports.get_history') ?>",
            type: 'get',
            data: {
                user_id: "{{ Auth::check() ? Auth::user()->id :  '' }}"
            }
        }).done(async function(res) {
            let result = JSON.parse(res);
            $('#sports-bet-history').empty();
            if (result.length) {
                for (let item of result) {
                    let html = `
                        <tr>
                            <td>${item.betsId}</td>
                            <td>${item.createdAt}</td>
                            <td>${item.SportName}</td>
                            <td>
                            <div>${item.home}</div>
                            <div>${item.away}</div>
                            <div>${item.league}</div>
                            </td>
                            <td>${item.odds}</td>
                            <td>${item.stake}</td>
                            <td>${item.profit ? item.profit : 0}</td>
                            <td>${item.status}</td>
                            <td>${item.betType}</td>
                        </tr>
                    `;
                    $('#sports-bet-history').append(html);
                }
            } else {
                let html = `<tr><td colspan="8">No data available in table</td></tr>`;
                $('#sports-bet-history').append(html);
            }
        }).fail(function(e) {
            console.log(e);
            toastr.error('server error.');
        })
    })
</script>
