<script>
    $(document).ready(function() {
        let location = window.location.pathname.split('/');
        if (location[1] == 'home') {
            $('.Tabs__list').addClass('Tabs__1');
            $('.Tabs__list li:nth-child(1)').addClass('Tabs__list-item--active')
        } else if (location[2] == 'live') {
            $('.Tabs__list').addClass('Tabs__2')
            $('.Tabs__list li:nth-child(2)').addClass('Tabs__list-item--active')
        } else if (location[1] == 'categories') {
            $('.Tabs__list').addClass('Tabs__4')
            $('.Tabs__list li:nth-child(4)').addClass('Tabs__list-item--active')
        }
    })

    $('.betslip-menu').click(function(){
        if($(this).hasClass('active')) {
            $(this).removeClass('active');
            $('.sports-aside-right').hide()
        } else {
            $(this).addClass('active');
            $('.sports-aside-right').show()
        }
    })
</script>
